import React from "react";
import RowControl from "./RowControl";

class Card extends React.Component {
  state = {
    item: this.props.item
  };

  getName = () => {
    return (
      <div>
        {this.props.item["name"].charAt(0).toUpperCase() +
          this.props.item["name"].slice(1)}
      </div>
    );
  };

  renderData = () => {
    if (this.state.item !== undefined) {
      Object.keys(this.state.item).map(key => {
        console.log(this.state.item[key]);
        return (
          <tr key={key + this.state.item[key]}>
            <td>{key}</td>
            <td>{this.state.item[key]}</td>
          </tr>
        );
      });
    }
  };

  render() {
    return (
      <div className="card">
        <div className="content">
          <div className="header">{this.getName()}</div>
          <div className="description">
            <table className="ui celled table">
              <tbody>{this.renderData()}</tbody>
            </table>
          </div>
        </div>
        <div
          className="ui bottom attached button"
          style={{ paddingTop: "5px", paddingBottom: "5px" }}
        >
          <RowControl />
        </div>
      </div>
    );
  }
}

export default Card;
