import React from "react";

class InputRow extends React.Component {
  state = { item: this.props.data, keyList: [], key: "", value: "" };

  componentDidMount = () => {
    if (this.state.item) {
      this.setState({ keyList: Object.keys(this.props.data) });
    } else {
      this.setState({ keyList: ["Name"] });
    }
  };

  onInputChange = event => {
    this.setState({ term: event.target.value });
  };

  addNewKey = key => {
    this.setState(prevState => ({
      keyList: [...prevState.keyList, key]
    }));
  };

  onFormSubmit = event => {
    event.preventDefault();
    console.log(event.target.value);
  };

  render() {
    return (
      <form className="ui equal width form" onSubmit={this.onFormSubmit}>
        <div className="fields">
          <div className="field">
            <select className="ui selection dropdown">
              <option value="">Choose a key</option>
              {this.state.keyList.map(key => {
                return (
                  <option key={key} value={key}>
                    {key}
                  </option>
                );
              })}
            </select>
          </div>

          <div className="field">
            <input
              type="text"
              value={this.state.key}
              onChange={this.onInputChange}
              placeholder="Enter a value"
            />
          </div>

          <div type="submit" className="ui button">
            Submit
          </div>
        </div>
      </form>
    );
  }
}

export default InputRow;
