import React from "react";
import Popup from "reactjs-popup";
import InputRow from "./elements/InputRow";

class EditWindow extends React.Component {
  state = {
    data: this.props.data,
    keys: [],
    editValues: this.props.data,
    selectedKey: ""
  };

  componentDidMount() {
    if (this.props.data === undefined || this.props.data.length === 0) {
      this.setState({ keys: ["Name"] });
    } else {
      this.setState({
        keys: Object.keys(this.props.data)
      });
    }
  }

  addNewKey = key => {
    this.setState(prevState => ({
      keys: [...prevState.keys, key]
    }));
  };

  render() {
    return (
      <Popup
        trigger={
          <button className="ui blue icon button">{this.props.button}</button>
        }
        modal
      >
        {close => (
          <div
            className="modal"
            style={{ padding: "10px", paddingTop: "25px" }}
          >
            <div className="ui grid">
              <div className="ui grid row">
                <div className="actions">
                  <button
                    className="ui blue compact button"
                    onClick={() => {
                      console.log("save");
                      close();
                    }}
                    style={{ verticalAlign: "true" }}
                  >
                    Save
                  </button>

                  <button
                    className="ui red compact button"
                    onClick={() => {
                      console.log("cancel");
                      close();
                    }}
                    style={{ verticalAlign: "true" }}
                  >
                    Cancel
                  </button>
                </div>
              </div>
            </div>
            <div className="row">
              <InputRow currentKey={this.selectedKey} />
            </div>
          </div>
        )}
      </Popup>
    );
  }
}

export default EditWindow;
