import React from "react";
import Card from "./elements/CardFunction";

class CardList extends React.Component {
  state = { data: [] };

  componentDidMount() {
    fetch("http://127.0.0.1:5000/item/apple")
      .then(res => res.json())
      .then(items => {
        this.setState({ data: items });
        console.log("state", this.state.data[0]);
      })
      .catch(console.log);

      var i = 0,
        result = [];

      while (i < this.state.data.length) {
        result.push([]);
        for (var key in this.state.data[i].fields) {
          result[result.length - 1].push(items[i].fields[key]);
        }
        i++;
      }

      document.write(JSON.stringify(result, null, 4));
  }

  renderCards = () => {
    this.state.data.map(item => {
      return (
        <div>
          <Card data={item} />
        </div>
      );
    });
  };

  render() {
    return <div className="ui container">{this.renderCards()}</div>;
  }
}

export default CardList;
