import React from "react";
import Popup from "reactjs-popup";
import DropDown from "./DropDown";
import AddNewButton from "./AddNewButton";
import InputBox from "./InputBox";

class EditWindow extends React.Component {
  /* Some test data for now */
  state = { data: [], keys: ["Name", "Quantity", "Price"] };

  addNewKey = key => {
    this.setState(prevState => ({
      keys: [...prevState.keys, key]
    }));
  };

  render() {
    return (
      <div>
        <Popup trigger={<button className="ui button">Add New</button>} modal>
          {close => (
            <div className="modal" style={{ padding: "10px" }}>
              <div className="ui header"> Add New </div>
              <div className="actions">
                renderButtons();
              </div>
              <div className="content">
                <div className="ui grid">
                  <div
                    className="one wide column"
                    style={{ paddingLeft: "2px" }}
                  >
                    <AddNewButton addNewFunc={this.addNewKey} typeName="Key" />
                  </div>

                  <div
                    className="seven wide column"
                    style={{ paddingRight: "2px" }}
                  >
                    <DropDown data={this.state.keys} typeName="Key" />
                  </div>

                  <div className="six wide column">
                    <InputBox />
                  </div>
                </div>
              </div>
            </div>
          )}
        </Popup>
      </div>
    );
  }
}

export default EditWindow;
