import React from "react";
import DropDown from "./DropDown";
import AddNewButton from "./AddNewButton";
import InputBox from "./InputBox";

class InputRow extends React.Component {
  state = {
    keys: ["Apples", "Oranges", "Pears"]
  };

  addNewKey = key => {
    this.setState(prevState => ({
      keys: [...prevState.keys, key]
    }));
  };

  render() {
    return (
      <div className="ui grid">
        <div className="one wide column" style={{ paddingRight: "0px" }}>
          <AddNewButton addNewFunc={this.addNewKey} typeName="Key" />
        </div>

        <div className="seven wide column" style={{ paddingRight: "2px" }}>
          <DropDown data={this.state.keys} typeName="Key" />
        </div>

        <div className="six wide column">
          <InputBox />
        </div>
      </div>
    );
  }
}

export default InputRow;
