import React from "react";
import RowControl from "./RowControl";

class Table extends React.Component {
  state = { keys: [], values: [] };

  generateKeyLists = () => {
    this.setState({ keys: ["Name", "Quantity", "Price"] });
  };

  generateValueLists = () => {
    this.setState({ values: ["Apple", "102", "$1.13"] });
  };

  renderLists = () => {
    return (
      <tbody>
        <tr>
          <td data-label="Key">
            this.state.keys.map({key => console.log(key)});
          </td>
        </tr>
        <tr>
          <td data-label="Value">
            this.state.values.map({val => console.log(val)});
          </td>
        </tr>
        <tr>
          <td data-label="Controls">
            <RowControl />
          </td>
        </tr>
      </tbody>
    );
  };

  render() {
    return (
      <table className="ui celled table">
        <thead>
          <tr>
            <th className="seven wide">Key</th>
            <th className="seven wide">Value</th>
            <th className="two wide">Controls</th>
          </tr>
        </thead>
        {this.renderLists()}
      </table>
    );
  }
}

export default Table;
