import React from "react";
import RowControl from "./RowControl";

const Card = ({ data }) => {
  if (!data) {
    return <div>Card data not found</div>;
  }

  const getIndex = (dict, query) => {
    for (var i in dict) {
      if (dict[i][0] === query) return i;
    }
    return -1;
  };

  const getName = () => {
    if (this.getIndex(data, "Name") === -1) {
      return <div>Error: Name not found</div>;
    }
    return data[getIndex(data, "Name")][1];
  };

  const renderData = () => {
    return data.map(entry => {
      return (
        <tr key={entry[0] + entry[1]}>
          <td>{entry[0]}</td>
          <td>{entry[1]}</td>
        </tr>
      );
    });
  };

  return (
    <div className="ui cards">
      <div className="card">
        <div className="content">
          <div className="header">{getName()}</div>
          <div className="description">
            <table className="ui celled table">
              <tbody>{renderData()}</tbody>
            </table>
          </div>
        </div>
        <div
          className="ui bottom attached button"
          style={{ paddingTop: "5px", paddingBottom: "5px" }}
        >
          <RowControl />
        </div>
      </div>
    </div>
  );
};

export default Card;
