import React from "react";
import Card from "./elements/Card";

class CardList extends React.Component {
  state = { data: [] };

  componentDidMount() {
    fetch("http://127.0.0.1:5000/item/apple")
      .then(res => res.json())
      .then(db => {
        this.setState({ data: db });
      })
      .catch(console.log);
  }

  renderCards = () => {
    for (var i = 0; i < this.state.data.length; i++) {
      return <Card data={this.state.data[i]} />;
    }
  };

  render() {
    return <div className="ui container">{this.renderCards()}</div>;
  }
}

export default CardList;
