import React from "react";
import DropDown from "../io/DropDown";
import AddNewButton from "../io/AddNewButton";

class KeyList extends React.Component {
  state = {
    keys: ["Weight", "Colour", "Quantity"]
  };

  addNewkey = key => {
    this.setState(prevState => ({
      keys: [...prevState.keys, key]
    }));
  };

  renderList() {
    return this.state.keys.map(key => {
      return <div className="item">{key}</div>;
    });
  }

  render() {
    return (
      <div className="ui grid">
        <div className="twelve wide column">
          <DropDown keys={this.state.keys} />
        </div>

        <div className="four wide column">
          <AddNewButton newKey={this.addNewkey()} />
        </div>
      </div>
    );
  }
}

export default KeyList;
