import React from "react";

class KeyList extends React.Component {
  state = {
    keys: ["Weight", "Colour", "Quantity"]
  };

  addNewkey = key => {
    this.setState(prevState => ({
      keys: [...prevState.keys, key]
    }));
  };

  renderList() {
    return this.state.keys.map(key => {
      <div className="item">Arabic</div>;

      return <option value={key}>{key}</option>;
    });
  }

  render() {
    return (
      <div className="ui grid">
        <div className="twelve wide column">
          <div className="ui fluid floating dropdown labeled search icon button">
            <i className="key icon" />
            <span className="text">Select a Key</span>
            <div className="menu">{this.renderList()}</div>
          </div>
        </div>

        <div className="four wide column">
          <button className="ui icon button">
            <i className="plus icon" />
          </button>
        </div>
      </div>
    );
  }
}

export default KeyList;
