import React from "react";
import Popup from "reactjs-popup";
import DropDown from "./DropDown";
import AddNewButton from "./AddNewButton";

class EditWindow extends React.Component {
  state = {
    data: [
      [
        ["Name", "Apple"],
        ["Quantity", "14"],
        ["Type", "Macintosh"],
        ["Origin", "Ontario"]
      ],
      [
        ["Name", "Orange"],
        ["Quantity", "12"],
        ["Colour", "Macintosh"],
        ["Origin", "Ontario"]
      ]
    ]
  };
  render() {
    return (
      <div>
        <Popup trigger={<button className="ui button">Add New</button>} modal>
          {close => (
            <div className="modal" style={{ padding: "10px" }}>
              <div className="ui header"> Add New </div>
              <div className="content">
                <div className="ui grid">
                  <div className="fourteen wide column">
                    <DropDown data={this.state.keys} typeName="Key" />
                  </div>

                  <div className="two wide column">
                    <AddNewButton addNewFunc={this.addNewKey} typeName="Key" />
                  </div>
                </div>
              </div>
              <div className="actions">
                <button
                  className="button"
                  onClick={() => {
                    console.log("Save");
                    /* this is where I would pass the data up somewhere*/
                    close();
                  }}
                >
                  Save
                </button>

                <button
                  className="button"
                  onClick={() => {
                    console.log("Cancel");
                    close();
                  }}
                >
                  Cancel
                </button>
              </div>
            </div>
          )}
        </Popup>
      </div>
    );
  }
}

export default EditWindow;
