import React from "react";
import Popup from "reactjs-popup";
import InputRow from "./InputRow";

class EditWindow extends React.Component {
  /* Some test data for now */
  state = { data: [], keys: ["Name", "Quantity", "Price"] };

  addNewKey = key => {
    this.setState(prevState => ({
      keys: [...prevState.keys, key]
    }));
  };

  render() {
    return (
      <Popup trigger={<button className="button">Add New</button>} modal>
        {close => (
          <div className="modal">
            <div className="ui container" style={{ padding: "10px" }}>
              <div className="ui grid">
                <div className="ui row">
                  <div className="ui header four wide column">Add New Item</div>
                  <div className="twelve wide column">
                    <div className="actions">
                      <button
                        className="ui compact button"
                        onClick={() => {
                          console.log("Action canceled");
                          close();
                        }}
                        style={{ verticalAlign: "true" }}
                      >
                        Cancel
                      </button>
                    </div>
                  </div>
                </div>

                <div className="content ui grid row">
                  <InputRow />
                </div>
              </div>
            </div>
          </div>
        )}
      </Popup>
    );
  }
}

export default EditWindow;
