import React from "react";
import Card from "./elements/CardFunction";

class CardList extends React.Component {
  constructor() {
    super();
    this.state = {
      data: []
    };
  }

  componentDidMount() {
    fetch("http://127.0.0.1:5000/item/apple")
      .then(res => { res.json();
      })
      .then(data => {
        let items = data.res.map(item => {
          return <div>{item}</div>;
        });
        this.setState({ data: items });
        console.log("state", this.state.data);
      })
      .catch(console.log);
  }

  renderCards = () => {
    this.state.data.map(item => {
      return (
        <div>
          <Card data={item} />
        </div>
      );
    });
  };

  render() {
    return <div className="ui container">{this.state.data[0]}</div>;
  }
}

export default CardList;
