import React from "react";

class InputRow extends React.Component {
  state = { item: this.props.data , key: "", value:""};

  componentDidMount = () => {
    if (this.state.item) {
      this.setState({ keys: Object.keys(this.props.data) });
    } else {
      this.setState({ keys: ["Name"] });
    }
  };

  onFormSubmit = event => {
    event.preventDefault();
    console.log(event.target.value);
  };

  render() {
    return (
      <form className="ui equal width form" onSubmit={this.onFormSubmit}>
        <div className="fields">
          <div className="field">
            <select className="ui selection dropdown">
              <option value="">Choose a key</option>
              {this.state.keys.map(key => {
                return (
                  <option key={key} value={key}>
                    {key}
                  </option>
                );
              })}
            </select>
          </div>

          <div className="field">
            <input type="text" name="value" placeholder="Enter a value" />
          </div>

          <div type="submit" className="ui button">
            Submit
          </div>
        </div>
      </form>
    );
  }
}

export default InputRow;
