import React from "react";
import Card from "./elements/CardFunction";

class CardList extends React.Component {
  state = { data: [] };

  componentDidMount() {
    fetch("http://127.0.0.1:5000/item/apple")
      .then(res => res.json())
      .then(items => {
        this.setState({ data: items });
      })
      .catch(console.log);

    var i = 0,
      result = [];

    while (i < this.state.data.length) {
      result.push([]);
      for (var key in this.state.data[i].fields) {
        result[result.length - 1].push(this.state.data[i].fields[key]);
      }
      i++;
    }

    document.write(JSON.stringify(result, null, 4));
  }

  renderCards = () => {
    this.state.data.map(item => {
      return (
        <div>
          <Card data={item} />
        </div>
      );
    });
  };

  render() {
    return (
      <div className="ui container">
        <button
          onClick={() => {
            <Card data={this.state.data[0]} />;
            console.log("click me");
          }}
        >
          click here
        </button>
      </div>
    );
  }
}

export default CardList;
