import React from "react";
import Card from "./elements/CardFunction";

class CardList extends React.Component {
  state = { data: [] };

  componentDidMount() {
    fetch("http://127.0.0.1:5000/item/apple")
      .then(res => res.json())
      .then(items => {
        this.setState({ data: items });
      })
      .catch(console.log);
  }

  objToArr = arr => {
    var i = 0,
      result = [];

    while (i < arr) {
      result.push([]);
      for (var key in arr.fields) {
        result[result.length - 1].push(arr.fields[key]);
      }
      i++;
    }

    document.write(JSON.stringify(result, null, 4));
  };

  renderCards = () => {
    this.state.data.map(item => {
      console.log(item);
      // const itemArray = this.objToArray(item);
      return (
        <div>
          <Card data={item} />
        </div>
      );
    });
  };

  render() {
    return <div className="ui container">{this.renderCards()}</div>;
  }
}

export default CardList;
